insert.foreach:
INSERT INTO discount (
    menu_index,
    company,
    discount
) VALUES (
    #{item.menuIndex},
    #{item.company},
    #{item.discount}
)

mapper.delete:
DELETE FROM discount
WHERE menu_index = (
    SELECT index FROM menus WHERE category = #{category} AND code = #{code}
)

mapper.select:
SELECT * FROM discount
WHERE menu_index = (
    SELECT index FROM menus WHERE category = #{category} AND code = #{code}
)
