SELECT count, price, dc_price, name, user_record_index, code, menu_name_kr, receipt_status, opt_size, opt_type, update_date, cancel_date, purchase_date, canceled_date, index, receipt_id, purchase_type FROM purchases
WHERE #{from} <= update_date AND update_date <= #{to}
AND company = #{company}
AND (receipt_status = '1' OR receipt_status = '2') ORDER BY purchase_date DESC

SELECT count, price, dc_price, name, user_record_index, code, menu_name_kr, receipt_status, opt_size, opt_type, update_date, cancel_date, purchase_date, canceled_date, index, receipt_id, email, company, purchase_type FROM purchases
WHERE #{before} <= update_date AND update_date <= #{after}
AND user_record_index = #{userRecordIndex}
AND company = #{company}
ORDER BY purchase_date DESC

SELECT * FROM purchases
WHERE #{before} <= update_date AND update_date <= #{after}
AND user_record_index = #{userRecordIndex}
AND company = #{company}
AND purchase_type = #{purchaseType}
ORDER BY purchase_date DESC
LIMIT #{perPage} OFFSET (#{page} - 1) * #{perPage}

SELECT COUNT(*) FROM purchases
WHERE #{before} <= update_date AND update_date <= #{after}
AND user_record_index = #{userRecordIndex}
AND company = #{company}
